create sequence category_seq start with 1 increment by 50;
create sequence characteristic_seq start with 1 increment by 50;
create sequence characteristic_value_seq start with 1 increment by 50;
create sequence country_seq start with 1 increment by 50;
create sequence promo_seq start with 1 increment by 50;
create sequence shipping_method_seq start with 1 increment by 50;
create table address (is_default boolean, country_id bigint, created_at timestamp(6), modified_at timestamp(6), address_line1 varchar(255), address_line2 varchar(255), city varchar(255), customer_id varchar(255) not null, id varchar(255) not null, postal_code varchar(255), region varchar(255), street_num varchar(255), unit_num varchar(255), primary key (id));
create table category (created_at timestamp(6) not null, id bigint not null, modified_at timestamp(6), parent_id bigint, description varchar(255), name varchar(255) not null, primary key (id), unique (name));
create table characteristic (category_id bigint not null, created_at timestamp(6) not null, id bigint not null, modified_at timestamp(6), data_type varchar(255), name varchar(255) not null, primary key (id), unique (name, category_id));
create table characteristic_value (characteristic_id bigint, created_at timestamp(6) not null, id bigint not null, modified_at timestamp(6), data varchar(255) not null, primary key (id), unique (data, characteristic_id));
create table country (id bigint not null, country_name varchar(255) unique, region varchar(255), primary key (id));
create table customer (is_deleted boolean, created_at timestamp(6) not null, deleted_at timestamp(6), modified_at timestamp(6), account_id varchar(255) unique, email varchar(255) unique, first_name varchar(255), id varchar(255) not null, last_name varchar(255), middle_name varchar(255), phone_num varchar(255), username varchar(255) unique, primary key (id));
create table order_item (price_per_item float(53), qty integer, total_amount float(53), created_at timestamp(6), modified_at timestamp(6), id varchar(255) not null, product_id varchar(255), productsku varchar(255) not null, shop_order_id varchar(255), primary key (id));
create table payment (amount float(53), created_at timestamp(6) not null, id varchar(255) not null, payment_method varchar(255), shop_order_id varchar(255), primary key (id));
create table product (price float(53) not null check(price >= 0.0), qty_in_stock integer not null check(price >= 0), category_id bigint, created_at timestamp(6) not null, deleted_at timestamp(6), modified_at timestamp(6), promo_id bigint, article varchar(255) not null unique, barcode varchar(255), brand varchar(255), description varchar(255), id varchar(255) not null, name varchar(255) not null, product_icon varchar(255), sku varchar(255) not null, product_images varchar(255) array, primary key (id));
create table product_characteristic_value_conf_m2m (characteristic_value_id bigint not null unique, product_id varchar(255) not null, primary key (characteristic_value_id, product_id));
create table promo (discount_rate float(53), created_at timestamp(6) not null, end_date timestamp(6), id bigint not null, modified_at timestamp(6), start_date timestamp(6), description varchar(255), name varchar(255), primary key (id));
create table review (rating float(53), created_at timestamp(6) not null, deleted_at timestamp(6), modified_at timestamp(6), customer_id varchar(255), id varchar(255) not null, product_id varchar(255), text varchar(255), primary key (id));
create table shipping_method (created_at timestamp(6) not null, id bigint not null, modified_at timestamp(6), name varchar(255), price varchar(255), primary key (id));
create table shop_order (total_amount float(53), approved_at timestamp(6), created_at timestamp(6) not null, modified_at timestamp(6), shipping_method_id bigint, customer_id varchar(255), id varchar(255) not null, shipping_address varchar(255), status varchar(255), primary key (id));
create table shopping_cart (total float(53) default 0.0 check(total >= 0.0) not null, created_at timestamp(6) not null, modified_at timestamp(6), customer_id varchar(255) unique, id varchar(255) not null, primary key (id));
create table shopping_cart_item (qty integer not null CHECK (qty > 0), created_at timestamp(6) not null, modified_at timestamp(6), id varchar(255) not null, product_id varchar(255) not null unique, shopping_cart_id varchar(255), primary key (id), unique (shopping_cart_id, product_id));